import React, { useEffect, useState } from "react";
import api from "../services/api";

function Dashboard() {
  const [notes, setNotes] = useState([]);
  const [note, setNote] = useState({ title: "", content: "" });

  const token = localStorage.getItem("token");

  const headers = {
    Authorization: `Bearer ${token}`,
  };

  const fetchNotes = async () => {
    try {
      const res = await api.get("/notes", { headers });
      setNotes(res.data);
    } catch (err) {
      console.error("Error fetching notes:", err);
    }
  };

  const handleAdd = async (e) => {
    e.preventDefault();
    try {
      await api.post("/notes", note, { headers });
      setNote({ title: "", content: "" });
      fetchNotes();
    } catch (err) {
      console.error("Error adding note:", err);
    }
  };

  const handleDelete = async (id) => {
    try {
      await api.delete(`/notes/${id}`, { headers });
      fetchNotes();
    } catch (err) {
      console.error("Error deleting note:", err);
    }
  };

  useEffect(() => {
    fetchNotes();
  }, []);

  return (
    <div style={{ padding: 20 }}>
      <h2>My Notes</h2>

      <form onSubmit={handleAdd}>
        <input
          type="text"
          placeholder="Title"
          value={note.title}
          onChange={(e) => setNote({ ...note, title: e.target.value })}
          required
        />
        <br />
        <textarea
          placeholder="Content"
          value={note.content}
          onChange={(e) => setNote({ ...note, content: e.target.value })}
          required
        />
        <br />
        <button type="submit">Add Note</button>
      </form>

      <hr />
      {notes.map((n) => (
        <div key={n._id} style={{ border: "1px solid gray", margin: "10px", padding: "10px" }}>
          <h3>{n.title}</h3>
          <p>{n.content}</p>
          <button onClick={() => handleDelete(n._id)}>Delete</button>
        </div>
      ))}
    </div>
  );
}

export default Dashboard;
