import React, { useState } from "react";
import api from "../services/api";
import { useNavigate } from "react-router-dom";
import "../Login.css"; // Optional styling

function Login() {
  const navigate = useNavigate();
  const [form, setForm] = useState({
    email: "",
    name: "",
    dob: "",
  });
  const [otp, setOtp] = useState("");
  const [step, setStep] = useState(1);

  const handleSendOtp = async () => {
    try {
      const res = await api.post("/auth/signup", form);
      alert(res.data.message);
      setStep(2);
    } catch (err) {
      alert("‚ùå Failed to send OTP");
    }
  };

  const handleVerify = async () => {
    try {
      const res = await api.post("/auth/verify", {
        email: form.email,
        otp,
      });
      localStorage.setItem("token", res.data.token);
      navigate("/dashboard");
    } catch (err) {
      alert("‚ùå Invalid OTP");
    }
  };

  return (
    <div className="login-container">
      <h2>üîê Login or Signup</h2>
      {step === 1 ? (
        <>
          <input
            type="text"
            placeholder="Full Name"
            value={form.name}
            onChange={(e) => setForm({ ...form, name: e.target.value })}
            required
          />
          <input
            type="email"
            placeholder="Email"
            value={form.email}
            onChange={(e) => setForm({ ...form, email: e.target.value })}
            required
          />
          <input
            type="date"
            placeholder="Date of Birth"
            value={form.dob}
            onChange={(e) => setForm({ ...form, dob: e.target.value })}
            required
          />
          <button onClick={handleSendOtp}>üì© Send OTP</button>
        </>
      ) : (
        <>
          <p>‚úÖ OTP sent to {form.email}</p>
          <input
            type="text"
            placeholder="Enter OTP"
            value={otp}
            onChange={(e) => setOtp(e.target.value)}
          />
          <button onClick={handleVerify}>‚úÖ Verify</button>
        </>
      )}
    </div>
  );
}

export default Login;
